#pragma once

#ifndef SUBSCRIPTIONOBJECT_H
#define SUBSCRIPTIONOBJECT_H

#include "StarWarsSchema.h"

namespace graphql::learn::object {
namespace methods::SubscriptionHas {

template <class TImpl>
concept getCharacterChangedWithParams = requires (TImpl impl, service::FieldParams params)
{
	{ service::AwaitableObject<std::shared_ptr<Character>> { impl.getCharacterChanged(std::move(params)) } };
};

template <class TImpl>
concept getCharacterChanged = requires (TImpl impl)
{
	{ service::AwaitableObject<std::shared_ptr<Character>> { impl.getCharacterChanged() } };
};

template <class TImpl>
concept getNewEpisodeWithParams = requires (TImpl impl, service::FieldParams params)
{
	{ service::AwaitableScalar<Episode> { impl.getNewEpisode(std::move(params)) } };
};

template <class TImpl>
concept getNewEpisode = requires (TImpl impl)
{
	{ service::AwaitableScalar<Episode> { impl.getNewEpisode() } };
};

template <class TImpl>
concept beginSelectionSet = requires (TImpl impl, const service::SelectionSetParams params)
{
	{ impl.beginSelectionSet(params) };
};

template <class TImpl>
concept endSelectionSet = requires (TImpl impl, const service::SelectionSetParams params)
{
	{ impl.endSelectionSet(params) };
};

} // namespace methods::SubscriptionHas

class [[nodiscard("unnecessary construction")]] Subscription final
	: public service::Object
{
private:
	[[nodiscard("unnecessary call")]] service::AwaitableResolver resolveCharacterChanged(service::ResolverParams&& params) const;
	[[nodiscard("unnecessary call")]] service::AwaitableResolver resolveNewEpisode(service::ResolverParams&& params) const;

	[[nodiscard("unnecessary call")]] service::AwaitableResolver resolve_typename(service::ResolverParams&& params) const;

	struct [[nodiscard("unnecessary construction")]] Concept
	{
		virtual ~Concept() = default;

		virtual void beginSelectionSet(const service::SelectionSetParams& params) const = 0;
		virtual void endSelectionSet(const service::SelectionSetParams& params) const = 0;

		[[nodiscard("unnecessary call")]] virtual service::AwaitableObject<std::shared_ptr<Character>> getCharacterChanged(service::FieldParams&& params) const = 0;
		[[nodiscard("unnecessary call")]] virtual service::AwaitableScalar<Episode> getNewEpisode(service::FieldParams&& params) const = 0;
	};

	template <class T>
	struct [[nodiscard("unnecessary construction")]] Model final
		: Concept
	{
		explicit Model(std::shared_ptr<T> pimpl) noexcept
			: _pimpl { std::move(pimpl) }
		{
			static_assert(methods::SubscriptionHas::getCharacterChangedWithParams<T>
				|| methods::SubscriptionHas::getCharacterChanged<T>
				|| methods::SubscriptionHas::getNewEpisodeWithParams<T>
				|| methods::SubscriptionHas::getNewEpisode<T>, R"msg(Subscription fields are not implemented)msg");
		}

		[[nodiscard("unnecessary call")]] service::AwaitableObject<std::shared_ptr<Character>> getCharacterChanged(service::FieldParams&& params) const override
		{
			if constexpr (methods::SubscriptionHas::getCharacterChangedWithParams<T>)
			{
				return { _pimpl->getCharacterChanged(std::move(params)) };
			}
			else if constexpr (methods::SubscriptionHas::getCharacterChanged<T>)
			{
				return { _pimpl->getCharacterChanged() };
			}
			else
			{
				throw service::unimplemented_method(R"ex(Subscription::getCharacterChanged)ex");
			}
		}

		[[nodiscard("unnecessary call")]] service::AwaitableScalar<Episode> getNewEpisode(service::FieldParams&& params) const override
		{
			if constexpr (methods::SubscriptionHas::getNewEpisodeWithParams<T>)
			{
				return { _pimpl->getNewEpisode(std::move(params)) };
			}
			else if constexpr (methods::SubscriptionHas::getNewEpisode<T>)
			{
				return { _pimpl->getNewEpisode() };
			}
			else
			{
				throw service::unimplemented_method(R"ex(Subscription::getNewEpisode)ex");
			}
		}

		void beginSelectionSet(const service::SelectionSetParams& params) const override
		{
			if constexpr (methods::SubscriptionHas::beginSelectionSet<T>)
			{
				_pimpl->beginSelectionSet(params);
			}
		}

		void endSelectionSet(const service::SelectionSetParams& params) const override
		{
			if constexpr (methods::SubscriptionHas::endSelectionSet<T>)
			{
				_pimpl->endSelectionSet(params);
			}
		}

	private:
		const std::shared_ptr<T> _pimpl;
	};

	explicit Subscription(std::unique_ptr<const Concept> pimpl) noexcept;

	[[nodiscard("unnecessary call")]] service::TypeNames getTypeNames() const noexcept;
	[[nodiscard("unnecessary call")]] service::ResolverMap getResolvers() const noexcept;

	void beginSelectionSet(const service::SelectionSetParams& params) const override;
	void endSelectionSet(const service::SelectionSetParams& params) const override;

	const std::unique_ptr<const Concept> _pimpl;

public:
	template <class T>
	explicit Subscription(std::shared_ptr<T> pimpl) noexcept
		: Subscription { std::unique_ptr<const Concept> { std::make_unique<Model<T>>(std::move(pimpl)) } }
	{
	}

	[[nodiscard("unnecessary call")]] static constexpr std::string_view getObjectType() noexcept
	{
		return { R"gql(Subscription)gql" };
	}
};

} // namespace graphql::learn::object

#endif // SUBSCRIPTIONOBJECT_H
